<template>
  <v-dialog
    v-model="getShow"
    width="400"
    persistent
    style="z-index: 200000000000000"
  >
    <v-card>

      <v-card-text
        class="
          blue
          lighten-5
          flex-column
          d-flex
          pt-5
          justify-center
          align-center
        "
        style="background-color: rgba(17, 85, 152, 5%) !important"
      >
        <div class="AvatarCustom">
          <span v-html="icon ? icons[icon] : icons.delete"> </span>
        </div>
        <p class="mb-0 text-h6 my-2 text-center">{{ $tr(textDialog) }}</p>
        <p class="mb-0 text-body-2">{{ $tr(descText) }}</p>
      </v-card-text>
      <v-card-actions class="justify-end my-1">
        <v-btn
          class="px-2"
          depressed
          @click="
            () => {
              cancelCallback();
              changeAlertModel(false);
            }
          "
          >{{ $tr("cancel") }}</v-btn
        >
        <v-btn
          depressed
          class="friqiBase white--text ms-2 px-2"
          @click="
            () => {
              acceptCallback();
              changeAlertModel(false);
            }
          "
          >{{ $tr(acceptbBtnTxt) }}</v-btn
        >
      </v-card-actions>
    </v-card>
  </v-dialog>
</template>
