                outlined
                counter
                auto-grow
              >
              </v-textarea>
              <div class="d-flex">
                <v-spacer></v-spacer>
                <v-btn
                  class="primary custom-btn"
                  style=""
                  @click="saveColumnSetting()"
                  :loading="loading"
                >
