<script>
export default {
  data() {
    return {
      showDialog: false,
      selected_subsystem: null,
      selected_column: {
        id: "",
        text: "",
        tooltip: "",
        category_id: "",
      },
      selected_category_id: null,
      tooltip: "",
      systems: [],
      skeleton: {
        style: "margin-bottom:0.2px",
        boilerplate: true,
        elevation: 0,
      },
      loading: false,
    };
  },
  methods: {
    async toggleDialog() {
      if (!this.showDialog && this.systems.length == 0) {
        this.showDialog = !this.showDialog;

        await this.fetchSystems();
        this.skeleton.boilerplate = false;
      } else {
        this.showDialog = !this.showDialog;
        this.skeleton.boilerplate = false;
      }
    },
    async fetchSystems() {
      try {
        const response = await this.$axios.get(
          "all-system-subsystem?with_columns=true"
        );
        this.systems = response?.data;
        this.systems = this.systems.filter(
          (row) => this.getSubSystems(row.sub_systems).length > 0
        );
      } catch (e) {}
    },
    selectSubsystem(data) {
      this.selected_column = { id: "", text: "", tooltip: "", category_id: "" };
      this.selected_subsystem = data;
    },
    checkIfSubSysemSelected(subsystems) {
      let flag = false;
      for (const item of subsystems) {
        if (this.selected_subsystem == item) {
          flag = true;
        }
      }
      return flag;
    },
    selectColumn(column) {
      this.selected_column = column;
    },
    checkIfColumnSelected(columns) {
      const data_set = new Set(columns);
      return data_set.has(this.selected_column);
    },
    getCategory() {
      return this.selected_column.id != ""
        ? this.selected_subsystem?.categories
        : [];
    },
    async saveColumnSetting() {
      this.loading = true;
      try {
        const response = await this.$axios.post(
          "save-column-changes",
          this.selected_column
        );
        if (response.status == 200) {
          this.$toasterNA("green", this.$tr('successfully_updated'));
        } else {
          // this.$toasterNA("red", this.$tr('something_went_wrong'));
			this.$toasterNA("red", this.$tr("something_went_wrong"));

        }
      } catch (e) {
        // this.$toasterNA("red", this.$tr('something_went_wrong'));
			this.$toasterNA("red", this.$tr("something_went_wrong"));

      }
      this.loading = false;
    },
    getSubSystems(sub_systems) {
      return sub_systems.filter((row) => row.tabs.length > 0);
    },
  },
};
</script>

